call.source:
File.prototype.__defineGetter__('contents', function() {
	this.open('rt');
	var text = this.read();
	this.close();
	return text;
});
File.prototype.__defineSetter__('contents', function(content) {
	if(content === false) return this.exists = false;
	this.open(content instanceof Blob ? 'wb' : 'wt');
	this.write(content);
	this.close();
});
